 Section 6 Calling rates In this script, we compute calling rates and test if the relationship between predicted counts of individuals and acoustic detections is governed by calling activity. 6.1 Load necessary libraries library(tidyverse) library(dplyr) library(stringr) library(vegan) library(scico) library(data.table) library(extrafont) library(sf) library(raster) library(scales) library(ggplot2) library(ggspatial) library(colorspace) library(lubridate) library(naniar) library(patchwork) library(ggrepel) 6.2 Load previous model object and filtered data load(&quot;results/glmm_model_results.RData&quot;) summary_stats &lt;- read.csv(&quot;results/glmm_model_abundance_detections.csv&quot;) data &lt;- read.csv(&quot;results/datSubset.csv&quot;) # we will also load the unfiltered acoustic data and point count data for calling rate calculation unfiltered_data &lt;- read.csv(&quot;results/pooled_pointCount_acoustic_data.csv&quot;) 6.3 Calculating calling rate Here, we estimate species-specific calling rate as the detections per minute per species and the corresponding visit. # we work with the unfiltered data first to extract a detections per minute column for each species per visit detections_per_minute &lt;- unfiltered_data %&gt;% filter(data_type == &quot;acoustic_data&quot;) %&gt;% mutate(minute = floor_date(as.POSIXct(begin_clock_time, format = &quot;%H:%M:%S&quot;), &quot;minute&quot;)) %&gt;% group_by(site_id, site_name, year, visit_number, scientific_name, common_name, minute) %&gt;% summarise(detections_per_minute = n(), .groups = &#39;drop&#39;) # we work with the unfiltered data first to extract a detections per 30s column for each species per visit # Alternative method using a custom 30-second flooring function detections_per_30s &lt;- unfiltered_data %&gt;% filter(data_type == &quot;acoustic_data&quot;) %&gt;% mutate( # Convert to POSIXct time_posix = as.POSIXct(begin_clock_time, format = &quot;%H:%M:%S&quot;), # Floor to the nearest 30-second interval bin_30s = floor_date(time_posix, &quot;30 seconds&quot;) ) %&gt;% group_by(site_id, site_name, year, visit_number, scientific_name, common_name, bin_30s) %&gt;% summarise(detections_per_30s = n(), .groups = &#39;drop&#39;) # let&#39;s add the detections per minute or rate of detections for a species per visit back to the filtered data data &lt;- left_join(data, detections_per_30s) %&gt;% replace_na(list(abundance = 0, detections_per_30s = 0)) # visualize calling rate across species fig_callingRate &lt;- data %&gt;% filter(common_name != &quot;Scarlet Tanager&quot;) %&gt;% # since it was excluded from the GLMM ggplot(., aes(x = common_name, y = detections_per_30s)) + geom_boxplot(fill = &quot;#D29C44FF&quot;) + #facet_wrap(~site_name, scales =&quot;free&quot;) + theme_bw(base_family = &quot;Century Gothic&quot;) + theme( plot.title = element_text(size = 18, face = &quot;bold&quot;), axis.title = element_text(size = 16, face = &quot;bold&quot;), axis.text.x = element_text( size = 12, angle = 45, hjust = 1, face = &quot;italic&quot; ), axis.text.y = element_text(size = 14), strip.text = element_text(size = 12, face = &quot;bold&quot;) ) + labs( x = &quot;Common Name&quot;, y = &quot;Calling rate (total detections per minute per visit)&quot;, title = &quot;Calling Rate by Species&quot; ) # Hermit thrush has the highest mean rate of calling per minute across the list of 21 species ggsave(fig_callingRate, filename = &quot;figs/callingRates-by-species.png&quot;, width = 12, height = 9, device = png(), units = &quot;in&quot;, dpi = 300) dev.off() # Load required libraries library(dplyr) library(ggplot2) library(tidyr) library(lubridate) library(patchwork) library(viridis) # Function to calculate detection rates for different temporal bins calculate_detection_rates &lt;- function(data, bin_durations_sec) { results &lt;- data.frame() for(bin_sec in bin_durations_sec) { # Convert bin duration to appropriate label if(bin_sec &lt; 60) { bin_label &lt;- paste0(bin_sec, &quot;s&quot;) } else if(bin_sec &lt; 3600) { bin_label &lt;- paste0(bin_sec/60, &quot;min&quot;) } else { bin_label &lt;- paste0(bin_sec/3600, &quot;hr&quot;) } # Create time bins based on the current bin duration data_binned &lt;- data %&gt;% filter(data_type == &quot;acoustic_data&quot;) %&gt;% mutate( # Convert time to seconds from start of day for easier binning time_seconds = as.numeric(as.POSIXct(begin_clock_time, format = &quot;%H:%M:%S&quot;) - as.POSIXct(&quot;00:00:00&quot;, format = &quot;%H:%M:%S&quot;)), # Create time bins time_bin = floor(time_seconds / bin_sec), bin_duration_sec = bin_sec, bin_label = bin_label ) %&gt;% group_by(site_id, site_name, year, visit_number, scientific_name, common_name, time_bin, bin_duration_sec, bin_label) %&gt;% summarise( detections_per_bin = n(), bin_start_time = min(time_seconds), .groups = &#39;drop&#39; ) %&gt;% mutate( # Convert to detections per minute for comparison detections_per_minute = detections_per_bin / (bin_duration_sec / 60) ) results &lt;- rbind(results, data_binned) } return(results) } # Function to calculate summary statistics for sensitivity analysis calculate_sensitivity_stats &lt;- function(binned_data) { # Calculate statistics for each species and bin duration summary_stats &lt;- binned_data %&gt;% group_by(scientific_name, common_name, bin_duration_sec, bin_label) %&gt;% summarise( mean_rate = mean(detections_per_minute, na.rm = TRUE), median_rate = median(detections_per_minute, na.rm = TRUE), sd_rate = sd(detections_per_minute, na.rm = TRUE), cv_rate = sd(detections_per_minute, na.rm = TRUE) / mean(detections_per_minute, na.rm = TRUE), q25_rate = quantile(detections_per_minute, 0.25, na.rm = TRUE), q75_rate = quantile(detections_per_minute, 0.75, na.rm = TRUE), iqr_rate = IQR(detections_per_minute, na.rm = TRUE), n_bins = n(), n_zero_bins = sum(detections_per_bin == 0), prop_zero_bins = n_zero_bins / n_bins, .groups = &#39;drop&#39; ) %&gt;% # Remove infinite CV values mutate(cv_rate = ifelse(is.infinite(cv_rate) | is.nan(cv_rate), NA, cv_rate)) return(summary_stats) } # Define temporal bin durations to test (in seconds) bin_durations &lt;- c(30, 60, 120, 240, 300, 600) # 30s to 20min bin_labels &lt;- c(&quot;30s&quot;, &quot;1min&quot;, &quot;2min&quot;, &quot;4min&quot;, &quot;5min&quot;, &quot;10min&quot;) # Calculate detection rates for all bin durations cat(&quot;Calculating detection rates for different temporal bins...
&quot;) unfiltered_data &lt;- unfiltered_data %&gt;% semi_join(data, by = &quot;common_name&quot;) binned_detection_data &lt;- calculate_detection_rates(unfiltered_data, bin_durations) # Calculate summary statistics summary_stats &lt;- calculate_sensitivity_stats(binned_detection_data) # Filter out Scarlet Tanager to match your analysis summary_stats_filtered &lt;- summary_stats %&gt;% filter(common_name != &quot;Scarlet Tanager&quot;) # 1. Heatmap showing coefficient of variation (precision) across species and bin durations p1_cv &lt;- summary_stats_filtered %&gt;% ggplot(aes(x = factor(bin_duration_sec, levels = bin_durations, labels = bin_labels), y = reorder(common_name, mean_rate))) + geom_tile(aes(fill = cv_rate), color = &quot;white&quot;, size = 0.1) + scale_fill_viridis_c(name = &quot;Coefficient of
